mapper.select:
select

from oil_article
where delete_status = 1 and type = ${type}
order by create_time desc limit ${limitSize}

mapper.insert:
insert into oil_article (id, article_id, user_id, 
  title, content, type, 
  delete_status, favourite, create_time
  )
values (#{id,jdbcType=VARCHAR}, #{articleId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, 
  #{title,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER}, 
  #{deleteStatus,jdbcType=INTEGER}, #{favourite,jdbcType=INTEGER}, #{createTime,jdbcType=INTEGER}
  )
