mapper.insert:
insert into article (id, article_id,user_id, article_type,
  title,sub_title,content,rates, rate_count, delete_status, create_time,update_time)
values (#{id,jdbcType=INTEGER}, #{articleId,jdbcType=VARCHAR},#{userId,jdbcType=VARCHAR}, #{articleType,jdbcType=INTEGER},
  #{title,jdbcType=VARCHAR},#{subTitle,jdbcType=VARCHAR},#{content,jdbcType=LONGVARCHAR},#{rates,jdbcType=INTEGER}, #{rateCount,jdbcType=INTEGER},
   #{deleteStatus,jdbcType=INTEGER}, #{createTime,jdbcType=INTEGER},#{updateTime,jdbcType=INTEGER}
  )

mapper.select:
select

from article
where delete_status = 0
and article_type = #{queryDto.articleType,jdbcType=INTEGER}
order by create_time desc
limit #{queryDto.page.pageNo},#{queryDto.page.pageSize}

select article_id, title,sub_title,create_time from article where delete_status = 0 and article_type = #{queryDto.articleType,jdbcType=INTEGER}
and user_id = #{queryDto.userId,jdbcType=VARCHAR}
order by create_time desc
