mapper.insert:
insert into article (id, article_id,user_id, article_type,
  title,content,rates, rate_count,is_hide, delete_status, create_time,update_time)
values (#{id,jdbcType=INTEGER}, #{articleId,jdbcType=VARCHAR},#{userId,jdbcType=VARCHAR}, #{articleType,jdbcType=INTEGER},
  #{title,jdbcType=VARCHAR},#{content,jdbcType=LONGVARCHAR},#{rates,jdbcType=INTEGER}, #{rateCount,jdbcType=INTEGER},
  #{isHide,jdbcType=INTEGER},#{deleteStatus,jdbcType=INTEGER}, #{createTime,jdbcType=INTEGER},#{updateTime,jdbcType=INTEGER}
  )

mapper.select:
select
from article
where delete_status != 1 and delete_status != 2 and is_hide = 0 and article_type = #{queryDto.articleType,jdbcType=INTEGER} order by create_time desc limit #{queryDto.page.pageNo},#{queryDto.page.pageSize}

select
user_id,article_id,title,is_hide,delete_status,create_time,rates
from article

where delete_status != 1
and article_type = #{queryDto.articleType,jdbcType=INTEGER}

    and user_id = #{queryDto.userId,jdbcType=VARCHAR}


  order by create_time desc


  order by update_time desc


    limit #{queryDto.page.pageNo},#{queryDto.page.pageSize}

select
count(*)
from article

where delete_status != 1
and article_type = #{queryDto.articleType,jdbcType=INTEGER}

    and user_id = #{queryDto.userId,jdbcType=VARCHAR}
