mapper.delete:
update micro_content
set delete_status = 1
where micro_id = #{microId,jdbcType=VARCHAR}

mapper.select:
select

from micro_content
where delete_status != 1



      and micro_content.user_id = #{queryDto.userId,jdbcType=VARCHAR}



      and micro_content.micro_type = #{queryDto.microType,jdbcType=INTEGER}

    order by create_time desc


      limit #{queryDto.page.pageNo},#{queryDto.page.pageSize}

select

from micro_content
where delete_status != 1



    and micro_content.user_id = #{queryDto.userId,jdbcType=VARCHAR}



    and micro_content.micro_type = #{queryDto.microType,jdbcType=INTEGER}

  order by create_time desc


    limit #{queryDto.page.pageNo},#{queryDto.page.pageSize}
